import logo from './logo.svg';
import React, { useEffect, useState } from 'react';
import './App.css';
import Footer from './components/Home/Footer';
import Header from './components/Home/Header';
import FormCreate from './components/Forms/FormCreate';
import FormList from './components/Forms/FormList';
import FormUpdate from './components/Forms/FormUpdate';

const App = () =>{
  const [selectdEntry, setSelectedEntry] = useState(null);
  const [listRefresh, setListRefresh] = useState(0);

  const refreshList = () => {
    setListRefresh(listRefresh + 1)
  }

  useEffect(() => {
    setSelectedEntry(null);
}, [listRefresh]);


  return (
    <div className="App">
      <div>
        <Header/>
          <div>
            <h2>Race Sign Up Form</h2>
            <FormCreate refreshList={refreshList}/>
            <FormList refreshList={refreshList}/>
              {selectdEntry && (
                <FormUpdate
                  selectedEntry={selectdEntry}
                  setSelectedEntry={setSelectedEntry}
                  refreshList={refreshList}
                />
              )}
          </div>
        <Footer/>
      </div>
    </div>
  );
}

export default App;
