import React, {useEffect, useState} from 'react';
import axios from 'axios';



const FormCreate = ({refreshList}) => {
    const url = 'http://localhost:8000/api/entries'
    //adding state to the functional component through useState React Hook and create form data
    const values = {
        id: 0,
        name: '',
        email: '',
        select: '',
        raceOption: '',
        comments: '',

    }
    
    const [entries, setEntries] = useState([]);
    const [entryData, setEntryData] = useState([values]);
    const [errors, setErrors] = useState([]);
    const [isSubmitting, setIsSubmitting] = useState(false);


    
    const handleChange = (e) => {
        const {name, value} = e.target;
        setEntryData({
            ...entryData, [name]: value,
        });
    };

    const validateEntry = () => {
        let entryErrors = {};
        if(!entryData.name.trim()) {
            entryErrors.name ="Name is required";
        }

        if (!entryData.email.trim()) {
            entryErrors.email = 'Email is required';
          } else if (!/\S+@\S+\.\S+/.test(entryData.email)) {
            entryErrors.email = 'Invalid email address';
          }
        
        if(!entryData.select) {
            entryErrors.select = 'Please select an option';
        }

        if(!entryData.raceOption) {
            entryErrors.raceOption ="Please select a race option";
        }

        if (!entryData.comments.trim()) {
            entryErrors.comments = 'Comments are required';
          }

        return entryErrors;
    };


    //creating the onSubmit function to submit the form to the backend
    const handleSubmit = (e) => {
            //using axios to call to the backend to create or post a new entry
            e.preventDefault();
            const entryErrors = validateEntry();
            setErrors(entryErrors);

            if(Object.keys(entryErrors).length === 0) {
                setIsSubmitting(true);

                if(entryData.id) {
                    axios.put(`${url}${entryData.id}`, entryData)
                    .then((response) => {
                        console.log('Entry updated successfully', response.data);
                        setIsSubmitting(false);
                        setEntryData(values);
                        refreshEntries();
                    })
                    .catch((error) => {
                        console.error('Error updating entry', error);
                        setIsSubmitting(false);
                    })
                } else {
                    axios.post(url, entryData)
                    .then((response) => {
                        console.log('Entry updated successfully', response.data);
                        setIsSubmitting(false);
                        setEntryData(values);
                        refreshEntries();
                    })
                    .catch((error) => {
                        console.error('Error updating entry', error);
                        setIsSubmitting(false);
                    });
                }    
            };
    };

    const handleEdit = (entry) => {
        setEntryData(entry);
    }

    const handleDelete = (id) => {
        axios.delete(`${url}${id}/`)
        .then((response) => {
            console.log('Entry deleted successfully:', response.data);
            refreshEntries();
        })
        .catch((error) => {
            console.error('Error deleting  entry:', error);
          });
    };

    const refreshEntries = () => {
        axios.get(url)
        .then((response) => {
            setEntries(response.data);
        })
        .catch((error) => {
            console.error('Error fetching entries:', error);
          })
    }

    useEffect(() => {
        refreshEntries();
      }, []);


    return(
        <div>
        <form className='form' onSubmit={handleSubmit}>
            <div className='formField'>
                <label className='label'>Name: </label>
                <input className='input' name='name' type='text' value={entries.name} onChange={handleChange} ></input>
                {errors.name && <div>{errors.name}</div>}
            </div>
            <div className='formField'>
                <label className='label'>Email: </label>
                <input className='input'type='text' name='email' value={entries.email} onChange={handleChange}></input>
                {errors.email && <div>{errors.email}</div>}
            </div>
            <div className='formField'>
                <label className='label'>Select: </label>
                <select value={entries.select} name="select" onChange={handleChange}>
                    <option value=''>Do you want your name on your bib?</option>
                    <option value='Yes'>Yes</option>
                    <option value='No'>No</option>
                </select>
                {errors.select && <div>{errors.selec}</div>}
            </div>
            <div className='formField'>
            <label className='label'>Choose a Race: </label>
                <label >
                    <input
                        type="radio"
                        onChange={handleChange}
                        name="raceOption"
                        value="5k"
                        checked={entries.raceOption === "5k"}
                        
                    />
                    5k
                </label>
                <label >
                    <input
                        type="radio"
                        onChange={handleChange}
                        name="raceOption"
                        value="Half-Marathon"
                        checked={entries.raceOption === "Half-Marathon"}

                    />
                    Half-Marathon
                </label>
                <label >
                    <input
                        type="radio"
                        onChange={handleChange}
                        name="raceOption"
                        value="Marathon"
                        checked={entries.raceOption === "Marathon"}
                    />
                    Marathon
                </label>
                {errors.raceOption && <div>{errors.raceOption}</div>}
            </div>
            <div className='formField'>
                <label className='label'>Comments: </label>
                <textarea className='text-area' name="comments" type='text' value={entries.comments} onChange={handleChange}></textarea>
                {errors.message && <div>{errors.comments}</div>}
            </div>
            <button className='submitButton' type='submit' onSubmit={handleSubmit}>Submit</button>
        </form>
            <h2>List of Enrtries</h2>
                <ul>
                {entries.map((entry) => (
                    <li key={entry.id}>
                        <div>
                            <strong>Name: </strong> {entry.name}
                        </div>
                        <div>
                            <strong>Email: </strong> {entry.email}
                        </div>
                        <div>
                            <strong>Select: </strong> {entry.select}
                        </div>
                        <div>
                            <strong>Choose a Race: </strong> {entry.select}
                        </div>
                        <div>
                            <strong>Comments: </strong> {entry.select}
                        </div>
                        <button onClick={() => handleEdit(entry)}>Edit</button>
                        <button onClick={() => handleDelete(entry.id)}>Delete</button>
                    </li>
                ))}
                </ul>
        </div>
    )

}

export default FormCreate;
