import React, {useState} from 'react';
import axios from 'axios';
import {useForm} from 'react-hook-form';


const FormCreate = ({refreshList}) => {

    //adding state to the functional component through useState React Hook and create form data
    const [formData, setFormData] = useState({ 
        name: '',
        email: '',
        select: '',
        raceOption: '',
        comments: '',
    })

    //using components of the useForm react hook to validate the inputs of the form
    const {
        register, 
        handleSubmit,
        formState: {errors},
        reset,
    } = useForm();

    


    //creating the onSubmit function to submit the form to the backend
    const onSubmit = (formData) => {
            //using axios to call to the backend to create or post a new entry
            axios.post('/api/create-entries/', formData) 
                .then((response) => {
                    //logging the data to the console 
                    console.log(response.data); 
                    reset();
                    refreshList();
                    alert('Form submitted successfully');
                })
                .catch((error) => {
                    console.error(error);
                    alert('An error occurred. Please try again.')
                });
        
    };

    return(
        <FormContainer>

        <form onSubmit={handleSubmit(onSubmit)}>
            <div>
                <label>Name: </label>
                <input type='text'{...register('name', {required: 'Name is required'})} ></input>
                {errors.name && <span>{errors.name.message}</span>}
            </div>
            <div>
                <label>Email: </label>
                <input type='text'{...register('email', {required: 'Email is required', pattern: {value: /^\S+@\S+\.\S+$/, message: "Please enter a valid email address"} })} ></input>
                {errors.email && <span>{errors.email.message}</span>}
            </div>
            <div>
                <label>Select: </label>
                <select {...register('selectValue', { required: 'Select an option' })} >
                    <option value=''>Do you want your name on your bib?</option>
                    <option value='Yes'>Yes</option>
                    <option value='No'>No</option>
                </select>
                {errors.selectValue && <span>{errors.selectValue.message}</span>}
            </div>
            <div>
            <label>Choose a Race: </label>
                <label>
                    <input
                        type="radio"
                        name="raceOption"
                        value="5k"
                        checked={formData.raceOption === "5k"}
                    />
                    5k
                </label>
                <label>
                    <input
                        type="radio"
                        name="raceOption"
                        value="Half-Marathon"
                        checked={formData.raceOption === "Half-Marathon"}
                        
                    />
                    Half-Marathon
                </label>
                <label>
                    <input
                        type="radio"
                        name="raceOption"
                        value="Marathon"
                        checked={formData.raceOption === "Marathon"}
                    />
                    Marathon
                </label>
            </div>
            <div>
                <label>Comments: </label>
                <textarea name="message" value={formData.message}></textarea>
            </div>
            <button type='submit'>Submit</button>
        </form>
        </FormContainer>
    )

}

export default FormCreate;
