import React, { useEffect, useState } from 'react';
import axios from 'axios';


const FormList = ({setSelectedEntry, refreshList}) => {
    
    const [entries, setEntries] = useState([]);

    useEffect(() => {
        refreshList();
    }, [refreshList]);

    const handleEdit = (entries) => {
        setSelectedEntry(entries);
    };

    const handleDelete = (entries) => {
        axios.delete(`/api/entries/${entries.id}`)
            .then((response) => {
                console.log(response.data);
                refreshList();
            })
            .catch((error) => {
                console.error(error);
            });
    };

    
    useEffect(() => {
        getEntries();
    }, []);

    const getEntries = () => {
        axios.get('/api/entries')
        .then((response) => {
            setEntries(response.data);
        })
        .catch((error) => {
            console.error(error);
        });
    };


    return (
        <div>
            <h2>List of Enrtries</h2>
                <ul>
                {entries.map((entry) => (
                    <li key={entry.id}>
                        <div>
                            <strong>Name: </strong> {entries.name}
                        </div>
                        <div>
                            <strong>Email: </strong> {entries.email}
                        </div>
                        <div>
                            <strong>Select: </strong> {entries.select}
                        </div>
                        <div>
                            <strong>Choose a Race: </strong> {entries.select}
                        </div>
                        <div>
                            <strong>Comments: </strong> {entries.select}
                        </div>
                        <button onClick={() => handleEdit(entries)}>Edit</button>
                        <button onClick={() => handleDelete(entries)}>Delete</button>
                    </li>
                ))}
                </ul>
        </div>
    )

}

export default FormList;
