import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {API_URL} from '../../constants';

const FormList = ({setSelectedEntry, refreshList}) => {
    const url = "http://localhost:8000/api/entries/"
    const [entries, setEntries] = useState([]);

    useEffect(() => {
        refreshList();
    }, [refreshList]);

    const handleEdit = (entry) => {
        setSelectedEntry(entry);
    };

    const handleDelete = (id) => {
        axios.delete(url)
            .then((response) => {
                console.log(response.data);
                refreshList();
            })
            .catch((error) => {
                console.error(error);
            });
    };

    const getFormEntries = () => {
        axios.get(API_URL)
        .then((response) => {
            setEntries(response.data);
        })
        .catch((error) => {
            console.error(error);
        });
    };

    useEffect(() => {
        getFormEntries();
    }, []);

    return (
        <div>
            <h2>List of Enrtries</h2>
                <ul>
                {entries.map((entry) => (
                    <li key={entry.id}>
                        <div>
                            <strong>Name: </strong> {entry.name}
                        </div>
                        <div>
                            <strong>Email: </strong> {entry.email}
                        </div>
                        <div>
                            <strong>Select: </strong> {entry.select}
                        </div>
                        <div>
                            <strong>Choose a Race: </strong> {entry.select}
                        </div>
                        <div>
                            <strong>Comments: </strong> {entry.select}
                        </div>
                        <button onClick={() => handleEdit(entry)}>Edit</button>
                        <button onClick={() => handleDelete(entry.id)}>Delete</button>
                    </li>
                ))}
                </ul>
        </div>
    )

}

export default FormList;
