import React, {useState} from 'react';
import axios from 'axios';



const FormCreate = ({refreshList}) => {
    const url = "http://localhost:8000/api/entries/"
    //adding state to the functional component through useState React Hook and create form data
    const [name, setName] =useState();
    const [email, setEmail] = useState();
    const [select, setSelect] = useState();
    const [raceOption, setRaceOption] = useState();
    const [comments, setComments] = useState();
    // const [entries, setEntries] = useState({ 
    //     id: 0,
    //     name: '',
    //     email: '',
    //     select: '',
    //     raceOption: '',
    //     comments: '',
    // })

    
    

    


    //creating the onSubmit function to submit the form to the backend
    const handleSubmit = (e) => {
            //using axios to call to the backend to create or post a new entry
            e.preventDefault();
            axios.post(url, name, email, select, raceOption, comments) 
                .then((response) => {
                    //logging the data to the console 
                    console.log(response.entries); 
                    refreshList();
                    alert('Form submitted successfully');
                })
                .catch((error) => {
                    console.error(error);
                    alert('An error occurred. Please try again.')
                });
        
    };

    return(
        <form className='form' onSubmit={handleSubmit}>
            <div className='formField'>
                <label className='label'>Name: </label>
                <input className='input' name='name' type='text' value={name} onChange={e =>setName(e.target.value)} ></input>
                
            </div>
            <div className='formField'>
                <label className='label'>Email: </label>
                <input className='input'type='text' name='email' value={email} onChange={e=>setEmail(e.target.value)}></input>
                
            </div>
            <div className='formField'>
                <label className='label'>Select: </label>
                <select value={select} name="select" onChange={handleChange}>
                    <option value=''>Do you want your name on your bib?</option>
                    <option value='Yes'>Yes</option>
                    <option value='No'>No</option>
                </select>
                
            </div>
            <div className='formField'>
            <label className='label'>Choose a Race: </label>
                <label >
                    <input
                        type="radio"
                        onChange={handleChange}
                        name="raceOption"
                        value="5k"
                        checked={entries.raceOption === "5k"}
                        
                    />
                    5k
                </label>
                <label >
                    <input
                        type="radio"
                        onChange={handleChange}
                        name="raceOption"
                        value="Half-Marathon"
                        checked={entries.raceOption === "Half-Marathon"}

                    />
                    Half-Marathon
                </label>
                <label >
                    <input
                        type="radio"
                        onChange={handleChange}
                        name="raceOption"
                        value="Marathon"
                        checked={entries.raceOption === "Marathon"}
                    />
                    Marathon
                </label>
                
            </div>
            <div className='formField'>
                <label className='label'>Comments: </label>
                <textarea className='text-area' name="comments" type='text' value={entries.comments} onChange={handleChange}></textarea>
                
            </div>
            <button className='submitButton' type='submit' onSubmit={handleSubmit}>Submit</button>
        </form>
    )

}

export default FormCreate;
